// C:\Users\aweso\Downloads\nasa-dashboard\frontend\src\Dashboard.js
import React, { useEffect, useState } from 'react';
import { fetchAPOD } from './api'; // Must match the export exactly

function Dashboard() {
  const [data, setData] = useState(null);
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchAPOD()
      .then((response) => {
        setData(response);
        setLoading(false);
      })
      .catch((err) => {
        setError(err.message || "Error fetching NASA data.");
        setLoading(false);
      });
  }, []);

  if (loading) return <div>Loading NASA data...</div>;
  if (error) return <div className="error">Error: {error}</div>;

  return (
    <div className="dashboard">
      <h2>{data.title}</h2>
      {data.media_type === 'image' ? (
        <img src={data.url} alt={data.title} style={{ maxWidth: '100%' }} />
      ) : (
        <iframe
          title="NASA Video"
          src={data.url}
          frameBorder="0"
          allow="autoplay; encrypted-media"
          allowFullScreen
        />
      )}
      <p>{data.date}</p>
      <p>{data.explanation}</p>
    </div>
  );
}

export default Dashboard;
